config = {
    // When true, the participant will join the conference in a muted state
    muted: !!workflow.scratchpad.isMuted,

    // When true, this attribute tells a conference call to start when this participant
    // joins the conference, if it is not already started. If this is set to false and
    // the participant joins a conference that has not started, all participants will be
    // muted until a participant joins that has 'startOnEnter' set to true
    startOnEnter: true,

    // When true, the conference call will end when the participant leaves and all
    // remaining participants will be dropped out of the conference call.
    endOnExit: false,
	
	// When true, the participant can exit the conference call by pressing "*" button
	hangupOnStar: true,

	// ----------------------------------------------------------------------------------------------
	// The following attributes are supported by Twilio Direct driver
	// but not by Notify Twilio Driver (Legacy)
	// ----------------------------------------------------------------------------------------------

	// Plays a beep when this user joins the conference
	beepOnEnter: true,
	// Plays a beep when this user leaves the conference
	beepOnExit: true,
	// Allows input of a single digit immediately after the conference ends for this user and sets that in workflow.scratchpad.confDigits. This option will be ignored if hangupOnStar is false.
	enableImmediateInput: false
};

