// send (re)join sms message
var from    = workflow.scratchpad.conference.notify_number;
var to      = workflow.scratchpad.conference.phone_number;
var body    = getMessageBody(workflow.scratchpad.conference_call, from);
//var task = new GlideRecord(workflow.scratchpad.conference.source_table);
//task.get(workflow.scratchpad.conference.source_sys_id);

new SNC.Notify().sendSMS(from, to, body, workflow.scratchpad.conference_call);

/**
 * Generate the welcome message for this conference call
 *
 * @param conference_call
 * @returns {string}
 */
function getMessageBody(conference_call, from) {
    // define message argumens
    var args = [conference_call.getDisplayValue(), conference_call.getValue('code'), from];

    // append source details if we have any
    if (GlideStringUtil.notNil(conference_call.source)) {
        // source record defined
        var displayValue = conference_call.source.getDisplayValue().replace(":", "");
        if (GlideStringUtil.notNil(displayValue)) {
            args.push(displayValue);
            return gs.getMessage('You have been added to conference call {0} for {3}. To (re)join the conference call, reply with {1} or send it via SMS to {2}.', args);
        }
    }

    return gs.getMessage('You have been added to conference call {0}. To (re)join the conference call, reply with {1} or send it via SMS to {2}.', args);
}

