answer = isValidConferenceCall();

/**
 * Check if the entered code matches a
 * @returns {*}
 */
function isValidConferenceCall() {
    // get digits from the input activity
    var code = workflow.scratchpad.digits;

    // get any conference call that matches
    var record = new GlideRecord('notify_conference_call');
    record.query('code', code);

    // is there a record?
    if (record.next() && record.getValue('active') == 1) {
        // store the conference GlideRecord on the scratchpad which is
        // required by the join conference call activity.
        workflow.scratchpad.conference_call = record;

        // store conference information on the scratchpad so we can use it in 
        // say, play and sms activities
        workflow.scratchpad.conference = {
            number          : record.getValue('number'),
            code            : record.getValue('code'),
            notify_number   : current.notify_number.phone_number,
            phone_number    : current.phone_number
        };

        return 'yes';
    }

    return 'no';
}

