text = generateTextToSay();

/**
 * Generate the text to say and the language to say it in
 *
 * @returns {{text: string, language: string}}
 */
function generateTextToSay() {
    // construct the result object
    return {
        // define the text to say
        text        : getMessageBody(workflow.scratchpad.conference_call),

        // and the language to say it in (see
        // or query the notify_language table
        // for supported languages)
        language    : 'en-US'
    };
}

/**
 * Generate the welcome message for this conference call
 *
 * @param conference_call
 * @returns {string}
 */
function getMessageBody(conference_call) {
    // append source details if we have any
    if (GlideStringUtil.notNil(conference_call.source)) {
        // source record defined
        var displayValue = conference_call.source.getDisplayValue().replace(":", "");
        if (GlideStringUtil.notNil(displayValue)) {
			return 'You have been added to a conference call for ' + displayValue;
        }
    }

    return 'You have been added to conference call ' + conference_call.getDisplayValue();
}

